mapper.select:

 FROM user AS a
WHERE a.school_year = #{schoolYear} 
  AND a.class_name = #{className}

mapper.insert:
INSERT INTO user
(
	 email
	, nickname
	, name
	, id
	, user_type
	, school_name
	, school_year
	, password
	, grade
	, register_time
	, approved_yn
)
VALUES 
( 
	 #{email}
	, #{nickname}
	, #{name}
	, #{userid}
	, #{userType}
	, #{schoolName}
	, #{schoolYear}
	, #{password}
	, #{grade}
	, now()
	, #{approvedYn}
);

mapper.update:
UPDATE user SET
	  email = #{email}
	, name = #{name}
	, nickname = #{userid}
	, school_year = #{schoolYear}
	, update_time = now()
	, mail_key = #{mailKey}
	, mail_auth = #{mailAuth}
 WHERE user_idx = #{userIdx}

UPDATE user SET
      password = #{password}
	, mail_key = #{mailKey}
	, mail_auth = #{mailAuth}
WHERE email = #{email}

UPDATE user SET 
	  approved_yn = 'Y' 
 WHERE user_idx = #{idx}

UPDATE user SET
	last_login_time = now()
 WHERE email = #{email}

UPDATE user SET 
	  password = #{password} 
 WHERE user_idx = #{idx}
